class union_find {
	vector<int> par;
	int find(int a) {
		return par[a] < 0 ? a : par[a] = find(par[a]);
	}
public:
	union_find(int n) : par(n, -1) {}
	bool same(int a, int b) {
		return find(a) == find(b);
	}
	void unite(int a, int b) {
		a = find(a);
		b = find(b);
		if (a == b) return;
		if (par[a] < par[b]) {
			par[a] += par[b];
			par[b] = a;
		}
		else {
			par[b] += par[a];
			par[a] = b;
		}
	}
	int size(int a) {
		return -par[find(a)];
	}
};

const int INF = 1e9;

struct edge {
	int u, v, cost;
	bool f;
	edge(int u_, int v_, int w, bool f_) : u(u_), v(v_), cost(w), f(f_) {}
	bool operator<(const edge& that) const {
		return cost < that.cost;
	}
};

int main()
{
	ios::sync_with_stdio(false), cin.tie(0);
	int n, m, k;
	while (cin >> n >> m >> k, n | m | k) {
		vector<edge> es;
		for (int i = 0; i < m; i++) {
			int u, v, w;
			char l;
			cin >> u >> v >> w >> l; u--, v--;
			es.emplace_back(u, v, w, l == 'A');
		}
		sort(es.begin(), es.end());
		int res = INF;
		for (int i = 0; i < m; i++) {
			union_find uf(n);
			int c1 = 0, c2 = 0, sum = 0;
			for (int j = i; j < m; j++) if (es[j].f) {
				if (!uf.same(es[j].u, es[j].v)) {
					uf.unite(es[j].u, es[j].v);
					sum += es[j].cost;
					c1++;
				}
			}
			for (int j = 0; j < m; j++) if (!es[j].f) {
				if (!uf.same(es[j].u, es[j].v)) {
					uf.unite(es[j].u, es[j].v);
					sum += es[j].cost;
					c2++;
				}
			}
			if (c1 == k && c2 == n - 1 - k) {
				res = min(res, sum);
			}
		}
		cout << (res == INF ? -1 : res) << endl;
	}
	return 0;
}

