//Matroid
//size(): returns size
//Get(): returns pair of
//1. indices of src(vertices that it is free to add)
//2. all (x,y) s.t. del x & add y is possible(y is not a src)
template<class LM,class RM>
struct MatroidIntersection{
	int n,m,s;
	LM& lm;
	RM& rm;
	struct Edge{
		int to,cost,id;
		friend ostream& operator<<(ostream&os,const Edge&e){
			return os<<"{T:"<<e.to<<", C:"<<e.cost<<", I:"<<e.id<<"}";
		}
	};
	V<tuple<int,int,int>> es;
	VV<Edge> g;
	MatroidIntersection(LM& llm,RM& rrm):lm(llm),rm(rrm){
		n=lm.size();
		m=rm.size();
		s=n+m;
	}
	void AddEdge(int a,int b,int c){
		assert(0<=a&&a<n);
		assert(0<=b&&b<m);
		assert(0<=c);
		es.eb(a,b,c);
	}
	vi h;
	V<pi> dist,prev;
	void FindShortest(const vi&src){
		dist.assign(s,pi(inf,-1));
		prev.assign(s,pi(-1,-1));

		using Node=tuple<int,int,int>;
		priority_queue<Node,V<Node>,greater<Node>> pq;
		const auto Reach=[&](int v,int d,int step,int j,int k){
			if(pi(d,step)<dist[v]){
				dist[v]=pi(d,step);
				prev[v]=pi(j,k);
				pq.push(make_tuple(d,step,v));
			}
		};

		for(auto i:src)
			Reach(i,0,0,-1,-1);

		while(!pq.empty()){
			int d,step,v;
			tie(d,step,v)=pq.top();
			pq.pop();
			if(dist[v]!=pi(d,step))
				continue;
			for(auto e:g[v]){
				assert(e.cost+h[v]-h[e.to]>=0);
				Reach(e.to,d+e.cost+h[v]-h[e.to],step+1,v,e.id);
			}
		}
	}
	int Augment(V<bool>&use){
		g.assign(s,V<Edge>());
		vi m1,m2;
		rep(i,es.size()){
			int a,b,c;
			tie(a,b,c)=es[i];
			if(use[i]){
				g[n+b].pb(Edge{a,-c,i});
				m1.pb(a);
				m2.pb(b);
			}else{
				g[a].pb(Edge{n+b,c,i});
			}
		}
		vi src,dst;
		{
			auto w=lm.Get(m1);
			src=w.first;
			if(src.empty())return -1;
			for(auto e:w.second)
				g[e.first].pb(Edge{e.second,0,-1});
		}
		{
			auto w=rm.Get(m2);
			dst=w.first;
			if(dst.empty())return -1;
			for(auto e:w.second)
				g[n+e.second].pb(Edge{n+e.first,0,-1});
		}

		FindShortest(src);

		pair<pi,int> w(pi(inf/2,-1),-1);
		for(auto i:dst)
			chmin(w,mp(dist[n+i],n+i));
		if(w.second==-1)
			return -1;
		int x=w.second;
		while(prev[x].first!=-1){
			int i=prev[x].second;
			if(i!=-1)
				use[i]=!use[i];
			x=prev[x].first;
		}
		int mn=w.first.first;
		rep(i,s){
			h[i]+=min(dist[i].first,mn);
			chmin(h[i],inf);
		}
		return h[w.second];
	}
	pair<int,vi> Solve(){
		h.assign(s,0);
		V<bool> use(es.size());
		int c=0,f;
		while((f=Augment(use))>=0)
			c+=f;
		vi sol;
		rep(i,es.size())
			if(use[i])
				sol.pb(i);
		return mp(c,sol);
	}
};

struct UnionFind{
	vi rank,par;
	void Init(int n){
		rank.assign(n,0);
		par.assign(n,-1);
	}
	int Find(int i){
		return par[i]==-1?i:(par[i]=Find(par[i]));
	}
	bool Same(int a,int b){
		return Find(a)==Find(b);
	}
	bool Unite(int a,int b){
		a=Find(a);
		b=Find(b);
		if(a==b)
			return false;
		if(rank[a]<rank[b])
			swap(a,b);
		par[b]=a;
		if(rank[a]==rank[b])
			rank[a]++;
		return true;
	}
};

struct MST{
	int n;
	vector<pi> es;
	MST(int nn){
		n=nn;
	}
	void AddEdge(int a,int b){
		assert(0<=a&&a<n);
		assert(0<=b&&b<n);
		es.eb(a,b);
	}
	int size(){
		return es.size();
	}
	UnionFind uf;
	pair<vi,V<pi>> Get(const vi&cur){
		uf.Init(n);
		vi t(size(),0);
		for(auto i:cur){
			uf.Unite(es[i].first,es[i].second);
			t[i]=1;
		}
		vi src;
		rep(i,size())if(!t[i]){
			if(!uf.Same(es[i].first,es[i].second)){
				src.pb(i);
				t[i]=2;
			}
		}
		V<pi> res;
		rep(i,size())if(t[i]==1){
			uf.Init(n);
			rep(j,size())if(t[j]==1&&j!=i)
				uf.Unite(es[j].first,es[j].second);
			rep(j,size())if(!t[j])
				if(!uf.Same(es[j].first,es[j].second))
					res.eb(i,j);
		}
		return mp(src,res);
	}
};

struct FreeMatroid{
	int n;
	FreeMatroid(int nn):n(nn){}
	int size(){
		return n;
	}
	pair<vi,V<pi>> Get(const vi&cur){
		V<bool> used(n);
		for(auto i:cur)
			used[i]=true;
		vi src;
		rep(i,n)if(!used[i])
			src.pb(i);
		return mp(src,V<pi>());
	}
};

signed main(){
	while(1){
		int n=read(),m=read(),k=read();
		if(n==0)break;
		MST mst(n);
		vi cost(m),type(m);
		rep(i,m){
			int u=read()-1,v=read()-1,w=read();
			string l=readString();
			mst.AddEdge(u,v);
			cost[i]=w;
			type[i]=l[0]-'A';
		}
		FreeMatroid fmat(n-1);
		MatroidIntersection<FreeMatroid,MST> matint(fmat,mst);
		rep(i,m){
			if(type[i]==0)
				rep(j,k)
					matint.AddEdge(j,i,cost[i]);
			else
				FOR(j,k,n-1)
					matint.AddEdge(j,i,cost[i]);
		}
		auto ans=matint.Solve();
		if(int(ans.second.size())<n-1)
			print(-1);
		else
			print(ans.first);
	}
}

