void init(int n) {
	for (int i = 0; i < n; i++) {
		par[i] = i;
		rank1[i] = 0;
	}
}
int find1(int x) {
	if (par[x] == x) {
		return x;
	}
	else {
		return par[x] = find1(par[x]);
	}
}
void unite1(int x, int y) {
	x = find1(x);
	y = find1(y);
	if (x == y)return;
	if (rank1[x] < rank1[y]) {
		par[x] = y;
	}
	else {
		par[y] = x;
		if (rank1[x] == rank1[y])rank1[x]++;
	}
}
bool same(int x, int y) {
	return find1(x) == find1(y);
}
struct edge { int u, v, cost,col; };
bool comp(const edge&e1, const edge&e2) {
	if (e1.cost != e2.cost) {
		return e1.cost < e2.cost;
	}
	return e1.col < e2.col;
}
edge cop[600];
int V, E;
int k;
P kruskal() {
	sort(cop, cop + E, comp);
	init(V);
	int res = 0; int cost = 0;
	for (int i = 0; i < E; i++) {
		edge e = cop[i];
		if (!same(e.u, e.v)) {
			unite1(e.u, e.v);
			cost += e.cost;
			if (e.col == 0) {
				res++;
			}
		}
	}
	return { res,cost };
}
