    int lb = -100000, ub = 100000;
    while(lb+1 < ub) {
      int mb = (lb+ub)/2;
      if(check(mb).second <= k) ub = mb;
      else lb = mb;
    }
    auto ans = check(ub);
    //cout<<ub<<endl;
    //cout<<ans.first<<" "<<ans.second << endl;
    if(ans.second == k) cout << ans.first << endl;
    else cout << -1 << endl;
  }

  return 0;
}

