template <int N>
struct union_find {
    int par[N];
    union_find() { for (int i = 0; i < N; ++i) par[i] = -1; }

    int find(int v) {
        if (par[v] < 0) return v;
        return par[v] = find(par[v]);
    }

    bool unite(int x, int y) {
        int xx = find(x), yy = find(y);
        if (xx == yy) return false;
        if (par[xx] < par[yy]) {
            par[xx] += par[yy];
            par[yy] = xx;
        } else {
            par[yy] += par[xx];
            par[xx] = yy;
        }
        return true;
    }

    bool same(int x, int y) {
        return find(x) == find(y);
    }
};

struct edge {
    int u, v, cost;
    char label;
    edge() {}
    edge(int u, int v, int cost, char label): u(u), v(v), cost(cost), label(label) {}

    bool operator < (const edge& o) const {
        return cost < o.cost;
    }
};

vector<edge> edges, edges_bak;

int N, M, K;

int solve() {
    edges_bak = edges;

    int ans = 1LL << 28;

    for (int a = -100; a <= 100; ++a) {
        edges = edges_bak;

        union_find<200> uf;

        for (edge& e : edges)
            if (e.label == 'A') e.cost += a;

        sort(begin(edges), end(edges));

        int weight_sum = 0, a_count = 0, b_count = 0;

        for (const edge& e : edges) {
            if (uf.same(e.u, e.v)) continue;
            if (e.label == 'A' && a_count == K) continue;
            if (e.label == 'B' && b_count == N-1-K) continue;
            (e.label == 'A' ? a_count : b_count)++;
            uf.unite(e.u, e.v);
            weight_sum += e.cost;
        }

        if (a_count + b_count < N-1) continue;

        if (a_count == K)
            ans = min(ans, weight_sum - K * a);
    }

    return (ans == 1 << 28 ? -1 : ans);
}


int main(void) {
    while (true) {
        cin >> N >> M >> K;
        edges.clear();
        edges_bak.clear();
        if (N == 0 && M == 0 && K == 0) break;
        for (int i = 0; i < M; ++i) {
            int u, v, cost; char label;
            cin >> u >> v >> cost >> label; --u; --v;
            edges.emplace_back(u, v, cost, label);
        }

        cout << solve() << endl;
    }
    return 0;
}

