        }
        int l = -200, r = 200;
        int ans = -1;
        while (l <= r) {
            int mid = (l + r) / 2;
            priority_queue<Pibi, vector<Pibi>, greater<Pibi>> q;
            vector<bool> vis(n);
            q.push(Pibi(Pib(0, false), 0));
            int sum = 0;
            int ca = 0;
