        for (int i = 0; i < edges.size(); ++i) {
            if (cnt >= n-1) break;
            Edge& e = edges[i];
            if (uf.isUnited(e.from, e.to)) continue;
            uf.unite(e.from, e.to);
            ans += e.cost;
            add_edge(G, e);
            ++cnt;
            s.insert(e.id);
            if (e.type == 'A') ++acnt;
            else ++bcnt;
        }
        if (cnt < n-1) {
            cout << -1 << endl;
            continue;
        }
        if (acnt > k) {
            swap(acnt, bcnt);
            swap(ae, be);
            k = n - k - 1;
        }
        // remove use edges
        // int s0 = 0;
        // for (int i = ea.size()-1; i >= 0; --i) {
        //     if (s.count(ea.id) > 0) {
        //         s0 = i+1;
        //         break;
        //     }
        // }
        vector<bool> used(ae.size(), false);
        for (int i = 0; i < ae.size(); ++i) {
            if (s.count(ae[i].id) > 0) {
                used[i] = true;
            }
        }
        assert(k-acnt >= 0);
        for (int t = 0; t < k-acnt; ++t) {
            Edge a = {0, 0, -1, -1, 'X'};
            int b = -1;
            for (int i = 0; i < ae.size(); ++i) {
                if (used[i]) continue;
                // cout << calc(G, ae[i]) << endl;
                Edge e = calc(G, ae[i]);
                if (e.cost >= 0 && (b < 0 || ae[i].cost - e.cost <= ae[b].cost - a.cost)) {
                    a = e;
                    b = i;
                }
            }
            if (b < 0) {
                ans = -1;
                break;
            }
            // if (a.cost >= inf) assert(false);
            // cout << a.cost << " " << b << endl;
            used[b] = true;
            remove_edge(G, a);
            add_edge(G, ae[b]);
            ans += ae[b].cost;
            ans -= a.cost;
        }
        cout << ans << endl;
    }
}

