                    f[i] = ri - 1;
                    t[i] = ri - 1;
                    w[i] = ri;
                    c[i] = sc.Char() - 'A';
                }
                Func<int, int[]> func = v => {
                    var cnt = 0;
                    var sum = 0;
                    var s = new DisjointSet(n);
                    foreach (var id in Enumerate(m, x => x).OrderBy(x => c[x] == 0 ? w[x] + v : w[x]).ThenBy(x => c[x])) {
                        if (s.Unite(f[id], t[id])) {
                            sum += c[id] == 0 ? w[id] + v : w[id];
                            cnt += c[id] ^ 1;
                        }
                    }
                    return new int[] { sum, cnt, s.Size(0) };
                };
                var l = -300; var r = 300;
                var ok = true;
                ok &= func(l)[2] == n;
                ok &= func(l)[1] >= k;
                ok &= func(r)[1] <= k;
                if (!ok) {
                    Console.WriteLine(-1);
                    continue;
                }
                while (r - l > 1) {
                    var x = (l + r) / 2;
                    if (func(x)[1] >= k) l = x;
                    else r = x;
                }
                var ans = func(l)[0] - l * k;
                Debug.WriteLine(func(l).AsJoinedString());
                Console.WriteLine(ans);

