using namespace std;

int a[100][100];
bool used[100][100];
int dx[4] = {1,0,-1,0},dy[4] = {0,1,0,-1};
int d,w,m;

int check(int x,int y){
  int res = 114514;
  for(int i = -1; i <= 1; i++){
    for(int j = -1; j <= 1; j++){
      if(i == 0 && j == 0) continue;
      if(used[x+i][y+j]) continue;
      res = min(res,a[x+i][y+j]);
    }
  }
  return res;
}

int dfs(int x,int y){
  used[x][y] = 1;
  m = max(m,a[x][y]);
  for(int i = 0; i < 4; i++){
    int nx = x+dx[i];
    int ny = y+dy[i];
    if(used[nx][ny]) continue;
    if((x == 0 || x == d-1) || (y == 0 || y == w-1)) continue;
    if(check(nx,ny) > check(x,y)) dfs(nx,ny);
  }

  int res = 0;
  for(int i = 0; i < d; i++){
    for(int j = 0; j < w; j++){
      if(used[i][j]) res += (m-a[i][j]);
    }
  }
  return res;
}

