using namespace std;

int h, w;
int a[10][10];

int getArea(int lx, int rx, int ly, int ry) {
	int i;
	int outMin = 1145141919;
	for (i = lx; i <= rx; i++) outMin = min(outMin, a[ly][i]);
	for (i = lx; i <= rx; i++) outMin = min(outMin, a[ry][i]);
	for (i = ly; i <= ry; i++) outMin = min(outMin, a[i][lx]);
	for (i = ly; i <= ry; i++) outMin = min(outMin, a[i][rx]);

	int ret = 0;
	for (int y = ly + 1; y < ry; y++) {
		for (int x = lx + 1; x < rx; x++) {
			if (a[y][x] >= outMin) { return 0; }
			ret += outMin - a[y][x];
		}
	}
	return ret;
}

