		for (lx = 0; lx < w; lx++) {
			for (rx = lx + 2; rx < w; rx++) {
				for (ly = 0; ly < h; ly++) {
					for (ry = ly + 2; ry < h; ry++) {
						ans = max(ans, getArea(lx, rx, ly, ry));
					}
				}
			}
