/*2重for文書くのめんどくなった時用だよ。

for (int i = 0; i < h; ++i) {
	for (int j = 0; j < w; ++j) {

	}
}

*/

int main() {

	while (1) {
		int d, w; cin >> d >> w;
		if (d == 0 && w == 0) { return 0; }

		int e[11][11];

		for (int i = 1; i <= d; i++) {
			for (int j = 1; j <= w; j++) {
				cin >> e[i][j];
			}
		}

		int ans = 0;

		for (int i = 1; i <= d; i++) {//高さ始点決め
			for (int j = 1; j <= w; j++) {//横始点決め
				for (int k = i + 2; k <= d; k++) {//高さ終点決め
					for (int l = j + 2; l <= w; l++) {//横終点決め

						int min_out = 9;
						int tmp = 0;
						bool missed = false;

						for (int x = i; x <= k; x++) {
							for (int y = j; y <= l; y++) {//2つのループで中身を見る

								bool in = true;
								if (x == i || x == k || y == j || y == l) { in = false; }

								if(in){}
								else {
									min_out = min(min_out, e[x][y]);//外堀の最小値を確認
								}

							}
						}

						for (int x = i+1; x < k; x++) {
							for (int y = j+1; y < l; y++) {//2つのループで中身を見る
								if (e[x][y] < min_out) { tmp += min_out - e[x][y]; }
								else { missed = true; }
							}
						}

						if(missed){}
						else { ans = max(ans, tmp); }

					}
				}
			}
		}


		cout << ans << endl;

	}

	return 0;
}

import java.util.*;
class C {
	public static void main(String arg[]){
		while(true){
			Scanner cs=new Scanner(System.in);
			int ans=0;
			int n=cs.nextInt();
			int m=cs.nextInt();
			if (n==0)break;
			int a[][]=new int[n+1][m+1];
			int s[][]=new int[n+1][m+1];
			for (int i=1;i<=n;i++)
				for(int j=1;j<=m;j++){
					a[i][j]=cs.nextInt();
				}
			for (int i=0;i<n;i++)
				for (int j=0;j<m;j++){
					s[i][j]=0;
				}
			for (int i=1;i<n;i++)
				for (int j=1;j<m;j++){
					s[i][j]=s[i][j-1]+s[i-1][j]-s[i-1][j-1]+a[i][j];
				}
			for (int st1=1;st1<n;st1++)
				for (int st2=1;st2<m;st2++)
					for (int en1=st1+2;en1<=n;en1++)
						for (int en2=st2+2;en2<=m;en2++){
							int min=a[st1][st2];
							for(int i=st1;i<=en1;i++){
								if (min>a[i][st2])min=a[i][st2];
								if (min>a[i][en2])min=a[i][en2];
							}
							for(int i=st2;i<=en2;i++){
								if (min>a[st1][i])min=a[st1][i];
								if (min>a[en1][i])min=a[en1][i];
							}
							int max=a[st1+1][st2+1];				
							for (int i=st1+1;i<en1;i++)
								for (int j=st2+1;j<en2;j++)
									if (max<a[i][j])max=a[i][j];
							if (max<min&& ans<min*(en1-st1-1)*(en2-st2-1)-(s[en1-1][en2-1]-s[st1][en2-1]-s[en1-1][st2]+s[st1][st2]))
								ans=min*(en1-st1-1)*(en2-st2-1)-(s[en1-1][en2-1]-s[st1][en2-1]-s[en1-1][st2]+s[st1][st2]);
						}
			System.out.println(ans);
		}
	}
}

