		vvi fsum(h+1, vi(w+1,0));
		rep(i, h)rep(j, w)cin >> f[i][j];
		int MIN[10][10][10][10];
		int MAX[10][10][10][10];
		rep(i, 10)rep(j, 10)rep(k, 10)rep(l, 10)MIN[i][j][k][l] = 0;
		rep(i, 10)rep(j, 10)rep(k, 10)rep(l, 10)MAX[i][j][k][l] = 0;
		rep(i, h)rep(j, w) {
			MIN[i][j][i][j] = f[i][j];
			MAX[i][j][i][j] = f[i][j];
			fsum[i + 1][j + 1] = f[i][j];
		}
		range(i,0, h + 1)range(j, 1, w + 1)fsum[i][j] += fsum[i][j - 1];
		range(i,1, h + 1)range(j, 0, w + 1)fsum[i][j] += fsum[i-1][j];
		//for (auto l : fsum)cout << l << endl;

		range(ylen,0,  h) rep(y, h - ylen ) {
			range(xlen,0, w ) rep(x, w - xlen) {
				if (ylen + xlen == 0)continue;
				//cout << pii(y, x) << "," << pii(y + ylen, x + xlen) << ":";
				if (ylen != 0) {
					MIN[y][x][y + ylen][x + xlen] = min(MIN[y][x][y + ylen - 1][x + xlen], MIN[y + ylen][x][y + ylen][x + xlen]);
					MAX[y][x][y + ylen][x + xlen] = max(MAX[y][x][y + ylen - 1][x + xlen], MAX[y + ylen][x][y + ylen][x + xlen]);
				}
				else {
					MIN[y][x][y + ylen][x + xlen] = min(MIN[y][x][y + ylen][x + xlen - 1], MIN[y + ylen][x + xlen][y + ylen][x + xlen]);
					MAX[y][x][y + ylen][x + xlen] = max(MAX[y][x][y + ylen][x + xlen - 1], MAX[y + ylen][x + xlen][y + ylen][x + xlen]);
				}
			}
		}
		int ans = 0;
		rep(ay, h)rep(ax, w)range(by,ay+2, h)range(bx,ax+2, w) {
			int inner = MAX[ay+1][ax+1][by-1][bx-1];
			int outer = INF;
			outer = min(outer, MIN[ay][ax][ay][bx]);
			outer = min(outer, MIN[ay][ax][by][ax]);
			outer = min(outer, MIN[ay][bx][by][bx]);
			outer = min(outer, MIN[by][ax][by][bx]);
			//cout << pii(ay, ax) << pii(by, bx) << outer << "," << inner << endl;
			if (outer > inner) {
				int high = fsum[by][bx] + fsum[ay + 1][ax + 1] - fsum[by][ax + 1] - fsum[ay + 1][bx];
				ans = max(ans, (by-ay-1)*(bx - ax - 1)*outer-high);
				//cout << pii(by, bx) << pii(ay, ax) << "=" << fsum[by][bx] + fsum[ay + 1][ax + 1] - fsum[by][ax + 1] - fsum[ay + 1][bx] << endl;
			}
		}
		cout << ans << endl;
		//cout << "=====" << endl;
	}
	return 0;
}

