ll kabe(ll x,ll y,ll x2,ll y2,vec2 &e) {
    ll gaisyuu = INF;
    FOR(i, x - 1, x2 + 2) {
        amin(gaisyuu, e[y - 1][i]);
    }
    FOR(i, x - 1, x2 + 2) {
        amin(gaisyuu, e[y2 + 1][i]);
    }

    FOR(i, y - 1, y2 + 2) {
        amin(gaisyuu, e[i][x - 1]);
    }

    FOR(i, y - 1, y2 + 2) {
        amin(gaisyuu, e[i][x2 + 1]);
    }

    return gaisyuu;
}

ll mizu(ll x,ll y,ll x2,ll y2,vec2 &e,ll gaisyuu){
    suiryou = 0;
    FOR(i,y,y2+1)FOR(j,x,x2+1){
        if(gaisyuu > e[i][j]){
            suiryou += gaisyuu - e[i][j];
        }else{
            return -1;
        }
    }
    return suiryou;
}


