                        for(int z = ulx; z <= lrx; z++) minwall = min(minwall,e[uly][z]);
                        for(int z = ulx; z <= lrx; z++) minwall = min(minwall,e[lry][z]);

                        int T = 0;
                        for(int iny = uly+1; iny < lry; iny++){
                            for(int inx = ulx+1; inx < lrx; inx++){
                                if(minwall <= e[iny][inx]) T = -INF;
                                T += minwall - e[iny][inx];
                            }
                        }
                        ans = max(ans,T);
                    }
                }
            }
        }
        cout << ans << endl;
    }
    return 0;
}

