int main() {
  int depth, width;
  int map[10][10];

  //reading a given map
  std::cin >> depth >> width;

  while(depth != 0 && width != 0) {
    //map initialization
    for(int i=0; i<10; i++)
      for(int j=0; j<10; j++) {
        map[i][j] = -1;
    }

    for(int i=0; i<depth; i++)
      for(int j=0; j<width; j++) {
        std::cin >> map[i][j];
    }

    int max_cap = 0;
    for(int level=1; level<10; level++)
      for(int i=1; i<depth-1; i++)
        for(int j=1; j<width-1; j++) {
          if(map[i][j] < level && map[i-1][j] >= level && map[i][j-1] >= level && map[i-1][j-1] >= level) {
            int i_start=i, j_start=j, i_start2, j_start2, i_end, j_end;
            int ii, jj;
            int flag = 0;

            //std::cout << "level:" << level << " i_s:" << i_start << " j_s:"<< j_start << std::endl;

            for(jj=j_start; map[i_start][jj+1] < level; jj++) {
              if(map[i_start-1][jj] < level || jj==width-1) { flag = 1; break; }
            }
            if(flag==1 || map[i_start-1][jj] < level || map[i_start-1][jj+1] < level) { continue; }
            else { j_end = jj; }
            //std::cout << "j_e:" << j_end << std::endl;

            for(ii=i_start; map[ii+1][j_end] < level; ii++) {
              if(map[ii][j_end+1] < level || ii==depth-2) { flag = 1; break; }
            }
            if(flag==1 || map[ii][j_end+1] < level || map[ii+1][j_end+1] < level) { continue; }
            else { i_end = ii; }
            //std::cout << "i_e:" << i_end << std::endl;

            for(jj=j_end; map[i_end][jj-1] < level; jj--) {
              if(map[i_end+1][jj] < level || jj==1) { flag = 1; break; }
            }
            if(flag==1 || map[i_end+1][jj] < level || map[i_end+1][jj-1] < level) { continue; }
            else { j_start2 = jj; }
            //std::cout << "j_s2:" << j_start2 << std::endl;

            for(ii=i_end; map[ii-1][j_start2] < level; ii--) {
              if(map[ii][j_start2-1] < level || ii==1) { flag = 1; break; }
            }
            if(flag==1 || map[ii][j_start2-1] < level || map[ii-1][j_start2-1] < level) { continue; }
            else { i_start2 = ii; }
            //std::cout << "i_s2:" << i_start2 << std::endl;

            if(i_start != i_start2 || j_start != j_start2) { continue; }

            int cap = 0;
            for(ii=i_start; ii<=i_end; ii++) {
              for(jj=j_start; jj<=j_end; jj++) {
                if(map[ii][jj] >= level) { flag = 1; break; }
                cap = cap + (level - map[ii][jj]);
              }
              if(flag==1) { break; }
            }
            if(flag==1) { continue; }

            if(max_cap < cap) max_cap = cap;
          }
    }

    std::cout << max_cap << std::endl;

    std::cin >> depth >> width;
  }

  return 0;
}

