// [l, r]の累積min
int h_accum_min(vector<vector<int> > &c, int y, int l, int r, int v, int h) {
    int ret = INF;
    for (int i = l; i <= r; ++i) {
        ret = min(ret, c[y][i]);
    }
    return ret;
}

// [u, d]の累積min
int v_accum_min(vector<vector<int> > &c, int x, int u, int d, int v, int h) {
    int ret = INF;
    for (int i = u; i <= d; ++i) {
        ret = min(ret, c[i][x]);
    }
    return ret;
}

int main(int argc, char const *argv[]) {
    while (true) {
        int d, w;
        cin >> d >> w;
        if (d == 0 && w == 0) break;
        vector<vector<int> > c(d, vector<int>(w));
        vector<vector<int> > v(d, vector<int>(w, 0));
        vector<vector<int> > h(d, vector<int>(w, 0));
        REP(i,d) {
            REP(j,w) {
                cin >> c[i][j];
            }
        }
        int ans = 0;
        // 縦横に動く左上
        for (int i = 0; i < d-2; ++i) {
            for (int j = 0; j < w-2; ++j) {
                // 右上は右に動くだけ。下降は左上に追随する
                for (int ru = j+2; ru < w; ++ru) {
                    // 左下は下に動くだけ。右に動くのは左上に追随する
                    for (int ld = i+2; ld < d; ++ld) {
                        int tmp = 0;
                        bool ok = true;
                        int U = h_accum_min(c, i, j, ru, d, w);
                        int D = h_accum_min(c, ld, j, ru, d, w);
                        int L = v_accum_min(c, j, i, ld, d, w);
                        int R = v_accum_min(c, ru, i, ld, d, w);
                        int MIN = min(U, min(D, min(L, R)));
                        // printf("i = %d, j = %d, ru = %d, ld = %d\n", i, j, ru, ld);
                        // cout << "MIN = " << MIN << endl;
                        // 内部を探索する。右下は左下と右上に追随するので記述しなくて良い
                        for (int in = i+1; in < ld; ++in) {
                            if (!ok) break;
                            for (int jn = j+1; jn < ru; ++jn) {
                                // printf("in = %d, jn = %d, c[in][jn] = %d\n", in, jn, c[in][jn]);
                                if (c[in][jn] >= MIN) {
                                    ok = false;
                                    break;
                                }
                                tmp += (MIN-c[in][jn]);
                                // cout << "tmp = " << tmp << endl;
                            }
                        }

                        if (ok) {
                            ans = max(ans, tmp);
                        }
                    }
                }
            }
        }

        cout << ans << endl;
    }
    return 0;
}

