    for (int lx = 0; lx < H; ++lx) {
        for (int ly = 0; ly < W; ++ly) {
            for (int rx = lx + 2; rx < H; ++rx) {
                for (int ry = ly + 2; ry < W; ++ry) {
                    ans = max(ans, cap(lx, ly, rx, ry));
                }
            }
        }
    }

    cout << ans << endl;
    return true;
}

