struct Pond
{
  int up,down,left,right;
  vector<int> canal;
  vector<int> moat;
  int min,max;
  void make(int up,int down,int left,int right,vector<int> canal,vector<int>moat)
  {
    this->up=up; this->down=down; this->left=left; this->right=right;
    this->canal=canal; this->moat=moat;
  }
  void setVals(){
    max= *max_element(canal.begin(),canal.end());
    min= *min_element(moat.begin(),moat.end());
  }
  bool isMake()
  {
    return (max<min);
  }
};

