                        for(int x=i+1; x<k; x++){
                            for(int y=j+1; y<l; y++){
                                if(e[x][y] >= limit){
                                    sub = 0;
                                    x = k;
                                    break;
                                }
                                sub += limit -e[x][y];
                            }
                        }
                        ans = max(ans, sub);
                    }
                }
            }
        }
        cout << ans << endl;
    }
    return 0;
}

