Int h, w;
vector<vector<Int> > mp;

Int calc(Int y,Int x, Int n,Int m){
  if(y + n - 1 >= h || x + m - 1 >= w) return -INF;

  Int height = INF;
  for(int i=0;i<n;i++) Min(height, mp[y + i][x]);
  for(int i=0;i<n;i++) Min(height, mp[y + i][x + m - 1]);
  for(int i=0;i<m;i++) Min(height, mp[y][x + i]);
  for(int i=0;i<m;i++) Min(height, mp[y + n - 1][x + i]);

  Int sum = 0;
  for(int i=0;i<n-2;i++)
    for(int j=0;j<m-2;j++){
      int num = mp[y + 1 + i][x + 1 + j];
      if(num >= height) return -INF;
      sum += (height - num);
    }
  return sum;
}

