using namespace std;
int main(int argc, char *argv[])
{
  for(;;) {
    int d, w;
    cin >> d >> w;
    if(d == 0 && w == 0) break;
    vector<vector<int>> es;
    for(int i = 0; i < d; i++) {
      vector<int> v;
      for(int j = 0; j < w; j++) {
        int c;
        cin >> c;
        v.push_back(c);
      }
      es.push_back(move(v));
    }
    auto f = [&es](int i, int j, int k, int l){
      int mn = es[i][j];
      for(int ii = i; ii <= k; ii++) {
        if(mn > es[ii][j]) mn = es[ii][j];
        if(mn > es[ii][l]) mn = es[ii][l];
      }
      for(int jj = j; jj <= l; jj++) {
        if(mn > es[i][jj]) mn = es[i][jj];
        if(mn > es[k][jj]) mn = es[k][jj];
      }
      int sum = 0;
      for(int ii = i + 1; ii < k; ii++) {
        for(int jj = j + 1; jj < l; jj++) {
          if(es[ii][jj] >= mn) return -1;
          sum += mn - es[ii][jj];
        }
      }
      return sum;
    };
    int mx = 0;
    for(int i = 0; i < d; i++) {
      for(int j = 0; j < w; j++) {
        for(int k = i + 2; k < d; k++) {
          for(int l = j + 2; l < w; l++) {
            int s = f(i, j, k, l);
            if(s < 0) continue;
            if(s > mx) mx = s;
          }
        }
      }
    }
    cout << mx << endl;
  }
  return 0;
}

