using namespace std;

#define REP(i,N) for(int i=0;i<(N);i++)
#define FOR(i,M,N) for(int i=M;i<(N);i++)
#define unko int

const int INF = 1 << 17;

int A[11][11];
int getmin(int x,int y,int X,int Y) {
	int mn = INF;
	FOR(i, x, X + 1) {
		mn = min(mn, A[i][y]);
		mn = min(mn, A[i][Y]);
	}
	FOR(j, y, Y + 1) {
		mn = min(mn, A[x][j]);
		mn = min(mn, A[X][j]);
	}
	return mn;
}
int getmax(int x, int y, int X, int Y) {
	int mx = -INF;
	FOR(i, x + 1, X) {
		FOR(j, y + 1, Y) {
			mx = max(mx, A[i][j]);
		}
	}
	return mx;
}
int calc(int x, int y, int X, int Y, int mn) {
	int res = 0;
	FOR(i, x + 1, X) {
		FOR(j, y + 1, Y) {
			if (A[i][j] >= mn)return 0;
			res += mn - A[i][j];
		}
	}
	return res;
}
int H, W;

void solve() {
	int ans = 0;
	REP(i, H - 2) {
		REP(j, W - 2) {
			FOR(k, i + 2, H) {
				FOR(l,j + 2, W) {
					int mn = getmin(i, j, k, l);
					int mx = getmax(i, j, k, l);
					if (mn > mx) {
						int res = calc(i, j, k, l, mn);
						ans = max(ans, res);
					}
				}
			}
		}
	}
	cout << ans << endl;
}

