using namespace std;

int f(vector<vector<int> > &e, int u, int d, int l, int r){

    int res = 0;
    int hight = 10;
    for(int i = u; i <= d; i++){
        hight = min(hight, e[i][l]);
        hight = min(hight, e[i][r]);
    }

    for(int j = l; j <= r; j++){
        hight = min(hight, e[u][j]);
        hight = min(hight, e[d][j]);
    }

    for(int i = u + 1; i < d; i++){
        for(int j = l + 1; j < r; j++){
            if(hight <= e[i][j]) return 0;
            res += hight - e[i][j];
        }
    }

    return res;
}

