			}
		}
		printf("\n");*/
		i=0;
		N=0;
		for(D=3;D<d+1;D++){//Dは長方形の高さ
			for(W=3;W<w+1;W++){//Wは長方形の幅
				for(y=0;y<d-D+1;y++){//yは長方形の左上のy座標
					for(x=0;x<w-W+1;x++){//xは長方形の左上のx座標
						//printf("%d %d %d %d\n\n",D,W,y,x);//現在の長方形の情報
						M=0;S=10;T=0;

						for(Y=y;Y<y+D;Y++){//Sは外枠の最小値
							if(Y==y||Y==y+D-1){
								for(X=x;X<x+W;X++){
									if(a[Y][X]<S){
										S=a[Y][X];
									}
								}
							}
							else {
								if(a[Y][x]<S){
									S=a[Y][x];
								}
								if(a[Y][x+W-1]<S){
									S=a[Y][x+W-1];
								}
							}
						}
						for(Y=y+1;Y<y+D-1;Y++){//Tは内側の最大値
							for(X=x+1;X<x+W-1;X++){
								if(a[Y][X]>T){
									T=a[Y][X];
								}
							}
						}
						//printf("%d %d\n\n",S,T);//今回の長方形のSとT
						if(S>T){                        //Mは今回の長方形の容量
							for(Y=y+1;Y<y+D-1;Y++){
								for(X=x+1;X<x+W-1;X++){
									M=M+S-a[Y][X];
								}
							}
						}
						if(N<M){//Nは今まで試した長方形の中の最大容量
							N=M;
						}
						i++;
					}
				}
			}
		}
		//printf("%d\n",i);//iは試行回数
		printf("%d\n",N);
	}
	return 0;
}

