using ll = int64_t;
template <typename T> using V = vector<T>;

struct Solver {
    ll H, W;
    V<V<ll>> vs;
    Solver() {
        cin >> H >> W;
        if(H + W) {
            vs = V<V<ll>>(H, V<ll>(W, 0));
            for(auto &v : vs) for(auto &ele : v) cin >> ele;
        }
    }

    ll calc(ll uh, ll dh, ll lw, ll rw) {
        ll lowest = 5e15;
        ll highest = 0;
        ll osum = 0;
        ll sum = 0;
        // cout << uh << ", " << dh << ", " << lw << ", " << rw << endl;
        for(ll h = uh; h < dh; h++) for(ll w = lw; w < rw; w++) {
            if(h == uh || h == dh - 1 || w == lw || w == rw - 1) {
                lowest = min(lowest, vs[h][w]);
            } else {
                highest = max(highest, vs[h][w]);
                sum += vs[h][w];
            }
        }
        // cout << highest << ", " << lowest << endl;
        if(highest >= lowest) return 0;
        ll ans = (dh - uh) * (rw - lw) * lowest - sum;
        ans -= ((dh - uh) + (rw - lw) - 2) * 2 * lowest;
        return ans;
    }

    bool solve() {
        if(!(H + W)) return false;
        ll ans = 0;
        for(ll lw = 0; lw < W; lw++) for(ll rw = lw + 3; rw <= W; rw++) {
            for(ll uh = 0; uh < H; uh++) for(ll dh = uh + 3; dh <= H; dh++) {
                ans = max(ans, calc(uh, dh, lw, rw));
            }
        }
        cout << ans << endl;
        return true;
    }
};

int main() {
    while(Solver().solve());
    return 0;
}

