int solve(int d, int w) {
    int e[d][w];
    for (int i = 0; i < d; i++) {
        for (int j = 0; j < w; j++) {
            cin >> e[i][j];
        }
    }
    int ans = 0;
    for (int x1 = 0; x1 < d; x1++) {
        for (int x2 = 0; x2 < d; x2++) {
            if (x2 - x1 < 2) continue;
            for (int y1 = 0; y1 < w; y1++) {
                for (int y2 = 0; y2 < w; y2++) {
                    if (y2 - y1 < 2) continue;
                    int inner_height = 0;
                    int surround_height = 100;
                    int inner_sum = 0;
                    int counter = 0;
                    for (int i = 0; i < d; i++) {
                        for (int j = 0; j < w; j++) {
                            if (x1 < i && i < x2 && y1 < j && j < y2) {
                                inner_height = max(inner_height, e[i][j]);
                                inner_sum += e[i][j];
                                counter++;
                            }
                            if (x1 <= i && i <= x2 && (j == y1 || j == y2)) {
                                surround_height = min(surround_height, e[i][j]);
                            }
                            if (y1 <= j && j <= y2 && (i == x1 || i == x2)) {
                                surround_height = min(surround_height, e[i][j]);
                            }
                        }
                    }
                    if (surround_height > inner_height) {
                        int tmp = counter * surround_height - inner_sum;
                        ans = max(ans, tmp);
                    }
                }
            }
        }
    }
    return ans;
}

