#define INF 1e9
int grid[20][20];
int d, w;
int calc(int top, int bottom, int left, int right) {
  int mi = INF;
  for (int i = left; i <= right; i++) {
    mi = min(grid[top][i], mi);
    mi = min(grid[bottom][i], mi);
  }
  for (int i = top; i <= bottom; i++) {
    mi = min(grid[i][left], mi);
    mi = min(grid[i][right], mi);
  }

  int ans = 0;
  for (int i = 0; i < bottom-top-1; i++) {
    for (int j = 0; j < right-left-1; j++) {
      if (mi <= grid[i+top+1][j+left+1]) {
	return -1;
      }
      ans += (mi-grid[i+top+1][j+left+1]);
    }
  }

  return ans;
}

signed main() {
  while (cin>>d>>w, d+w) {
    for (int i = 0; i < d; i++) {
      for (int j = 0; j < w; j++) {
	cin >> grid[i][j];
      }
    }
    int ans = -1;
    for (int i = 0; i < d; i++) {
      for (int j = d-1; j >= 0; j--) {
	for (int k = 0; k < w; k++) {
	  for (int l = w-1; l >= 0; l--) {
	    if (i == j || k == l) break;
	    ans = max(ans, calc(i, j, k, l));
	  }
	}
      }
    }
    cout << ans << endl;
  }
}

