int n, m;
int arr[20][20];

bool input() {
    scanf("%d %d", &n, &m);
    if (!n && !m) return 0;
    for (int i = 0 ; i < n ; i++ ) {
        for ( int j = 0 ; j < m ; j++ ) {
            scanf("%d", &arr[i][j]);
        }
    }
    return 1;
}

int check(int x, int y, int a, int b) {
    int out = 10000;
    for (int i = y ; i <= b ; i++ ) out = min(out, arr[x][i]);
    for (int i = y ; i <= b ; i++ ) out = min(out, arr[a][i]);
    for (int i = x+1 ; i < a ; i++ ) out = min(out, arr[i][y]);
    for (int i = x+1 ; i < a ; i++ ) out = min(out, arr[i][b]);

    int res = 0;
    for (int i = x+1 ; i < a ; i++ ) {
        for ( int j = y+1 ; j < b ; j++ ) {
            if ( arr[i][j] >= out  ) return 0;
            else res += out - arr[i][j];
        }
    }
    return res;
}

void solve() {
    int mx = 0;
    for (int i = 0 ; i < n ; i++ ) {
        for (int j = 0 ; j < m ; j++ ) {
            for (int a = i + 2 ; a < n ; a++ ) {
                for ( int b = j + 2 ; b < m ; b++ ) {
                    mx = max(mx, check(i, j, a, b));     
                }
            }
        }
    }
    cout <<mx << endl;
}

int main(){
    while(input())
    solve();
}

