int check(int a,int b,int c,int d){
	if(c-a<2 || d-b<2)return -1;
	int i,j,k;
	int p,q,r;//min_edge max_inter sum
	p=100,q=-1,r=0;
	for(i=a;i<=c;i++){
		for(j=b;j<=d;j++){
			if(a<i && i<c && b<j && j<d){
				if(q<t[i][j])q=t[i][j];
				r+=t[i][j];
			}else if(t[i][j]<p)p=t[i][j];
		}
	}
	if(p<=q)return -1;
	return p*(c-a-1)*(d-b-1)-r;
}


