          A[i].push_back(h);
        }
      }

      ll ma = 0;
      FOR(y0, 1, H-1){
        FOR(x0, 1, W-1){
          FOR(y1, y0, H-1){
            FOR(x1, x0, W-1){
              // 左上、右下が決まった

              ll pond_h = 0; // 池領域の最大高さ
              FOR(i, y0, y1+1){
                FOR(j, x0, x1+1){
                  chmax(pond_h, A[i][j]);
                }
              }

