int h, w;
int a[15][15];

int check(int sx, int sy, int tx, int ty) {
  int bd = 1e9;
  for (int y = sy; y <= ty; y++) {
    bd = min({bd, a[y][sx], a[y][tx]});
  }
  for (int x = sx; x <= tx; x++) {
    bd = min({bd, a[sy][x], a[ty][x]});
  }
  return bd;
}

int calc(int sx, int sy, int tx, int ty, int bd) {
  int ans = 0;
  for (int y = sy; y <= ty; y++) {
    for (int x = sx; x <= tx; x++) {
      if (bd <= a[y][x]) return 0;
      ans += bd - a[y][x];
    }
  }
  return ans;
}

int main() {
  while (true) {
    cin >> h >> w;
    if (h == 0 && w == 0) break;
    rep(y, h) rep(x, w) cin >> a[y][x];

    int ans = 0, bd;
    for (int sy = 0; sy < h - 2; sy++) {
      for (int sx = 0; sx < w - 2; sx++) {
        for (int ty = sy + 2; ty < h; ty++) {
          for (int tx = sx + 2; tx < w; tx++) {
            //池の境界の高さの最小値を求める
            bd = check(sx, sy, tx, ty);

            //容量を求める
            ans = max(ans, calc(sx + 1, sy + 1, tx - 1, ty - 1, bd));
          }
        }
      }
    }
    cout << ans << "\n";
  }
}

