int calc(int y1, int y2, int x1, int x2) {
    int outer = 1e9;
    // 壁の高さの最小値
    for (int i = y1; i <= y2; i++) {
        for (int j = x1; j <= x2; j++) {
            if (i == y1 || i == y2 || j == x1 || j == x2) {
                outer = min(outer, e[i][j]);
            }
        }
    }
    int res = 0;
    for (int i = y1; i <= y2; i++) {
        for (int j = x1; j <= x2; j++) {
            if (i == y1 || i == y2 || j == x1 || j == x2) {
                continue;
            }
            if (e[i][j] >= outer) return 0;
            res += outer - e[i][j];
        }
    }
    return res;
}

