                            else {
                                chmax(max_inside, board[x][y]);
                                sum += board[x][y];
                            }
                        }
                    }
                    if(max_inside < min_edge) {
                        int cand = min_edge * (h-2) * (w-2) - sum;
                        chmax(ans, cand);
                    }
                }
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}

