const int maxn=12;

int MX[maxn][maxn][maxn][maxn];
//from (i,j) to (j,k)
int mnl[maxn][maxn][maxn];
//from (i,j) up k
int mnh[maxn][maxn][maxn];
//front(i,j) right k
int sum[maxn][maxn],mx[maxn][maxn];

inline int getmin(int i,int j,int a,int b)
{
	int t=min(mnh[i][j][b-j],mnh[a][j][b-j]);
	int t2=min(mnl[a][j][a-i],mnl[a][b][a-i]);
	return min(t,t2);
}

inline int getsum(int i,int j,int a,int b)
{
	return sum[a][b]-sum[i-1][b]-sum[a][j-1]+sum[i-1][j-1];
}

int main()
{

	int n,m;;
	while(1)
	{
		cin>>n>>m;
		if(!n) break;

		rep(i,1,n) rep(j,1,m) {
			cin>>mx[i][j];	
			sum[i][j]=sum[i-1][j]+sum[i][j-1]-sum[i-1][j-1]+mx[i][j];
		}

		rep(i,1,n) rep(j,1,m){
			mnl[i][j][0]=mx[i][j];
			rep(k,1,i-1) mnl[i][j][k]=min(mnl[i][j][k-1],mx[i-k][j]);
		}

		rep(i,1,n) rep(j,1,m){
			mnh[i][j][0]=mx[i][j];
			for(int k=1;j+k<=m;++k) mnh[i][j][k]=min(mnh[i][j][k-1],mx[i][j+k]);
		}

		rep(i,1,n) rep(j,1,m){
			MX[i][j][i][j]=mx[i][j];
			rep(a,i+1,n) MX[i][j][a][j]=max(MX[i][j][a-1][j],mx[a][j]);
			rep(a,j+1,m) MX[i][j][i][a]=max(MX[i][j][i][a-1],mx[i][a]);
			rep(a,i+1,n) rep(b,j+1,m) MX[i][j][a][b]=max(max(MX[i][j][a-1][b],MX[i][j][a][b-1]),mx[a][b]);
		}

		int ans=0;
		rep(i,1,n) rep(j,1,m)//zuoshang
		{
			rep(a,i+2,n) rep(b,j+2,m)//youxia
			{
				int Min=getmin(i,j,a,b);
				if(Min <= MX[i+1][j+1][a-1][b-1]) continue;
				ans=max(ans,Min*((a-i-1)*(b-j-1))-getsum(i+1,j+1,a-1,b-1));
			}
		}
		cout<<ans<<endl;
	}
	return 0;
}

