#include<cstdio>
#include<algorithm>
#include<vector>
#include<queue>
#include<cstring>
using namespace std;

const int N = 10;

int a[N + 2][N + 2], n, m, used[N + 2][N + 2];

int in(int x, int y)
{
	if (x < 0 || x >= n || y < 0 || y >= m)
		return 0;
	return 1;
}


int test(int x, int y, int k)
{
	if (a[x][y] >= k)
		return 0;
	memset(used, 0, sizeof(used));
	queue<vector<int>> Q;
	int sum = k - a[x][y];
	used[x][y] = 1;
	int l = y, r = y, u = x, d = x;
	int cnt = 1;
	for (Q.push({ x, y }); !Q.empty(); Q.pop())
	{
		auto top = Q.front();
		for (int i = top[0] - 1; i <= top[0] + 1; i++)
			for (int j = top[1] - 1; j <= top[1] + 1; j++)
			{
				if (!in(i, j))
					continue;
				if (used[i][j])
					continue;
				if (a[i][j] >= k)
					continue;
				if (i == 0 || i == n - 1 || j == 0 || j == m - 1)
					return 0;
				//printf("%d %d %d %d\n", i, j, k, a[i][j]);
				l = min(l, j);
				r = max(r, j);
				u = min(u, i);
				d = max(d, i);
				used[i][j] = 1;
				cnt++;
				sum += k - a[i][j];
				Q.push({ i, j });
			}
	}
	if (cnt != (r - l + 1)*(d - u + 1))
		return 0;
	return sum;
}

int main()
{
	for (; scanf("%d%d", &n, &m) && (n || m); )
	{
		for (int i = 0; i < n; i++)
			for (int j = 0; j < m; j++)
				scanf("%d", &a[i][j]);
		int ans = 0;
		for (int i = 1; i < n-1; i++)
			for (int j = 1; j < m-1; j++)
				for (int k = 1; k <= 9; k++)
				{
					ans = max(ans, test(i, j, k));
					//printf("i=%d j=%d k=%d   %d\n", i, j, k, test(i, j, k));
				}
		printf("%d\n", ans);
	}
}

