using namespace std;
int N,M;
int a[12][12],ans;

int main () {
	while(1) {
			ans = 0;
		scanf("%d%d",&N,&M);
		if(N == 0) break;
		for(int i = 1;i <= N;++i) {
			for(int j = 1;j <= M;++j) {
				scanf("%d",&a[i][j]);
			}
		}

        for(int i1 = 1;i1 <= N;++i1) {
			for(int j1 = 1;j1 <= M;++j1) {
                for(int i2 = 1;i2 <= N;++i2) {
					for(int j2 = 1;j2 <= M;++j2) {


                        if(  (i1 - i2 > 0 ? i1 - i2 : i2 - i1) < 2 || (j1 - j2 > 0 ? j1 - j2 : j2 - j1) < 2)
							continue;

						int x1 = min(i1,i2);
						int y1 = min(j1,j2);
						int x2 = max(i1,i2);
						int y2 = max(j1,j2);


						int Min = 0x3f3f3f3f;

						for(int i = y1;i <= y2;++i) {
                            Min = min(Min,a[x1][i]);
                            Min = min(Min,a[x2][i]);
						}
						for(int i = x1;i <= x2;++i) {
                            Min = min(Min,a[i][y1]);
                            Min = min(Min,a[i][y2]);
						}

						int re = 0;bool f = 0;

						for(int i = x1 + 1;i <= x2 - 1;++i) {
							for(int j = y1 + 1;j <= y2 - 1;++j) {
                                re += Min - a[i][j];
                                if(a[i][j] >= Min) {
									f = 1;
									break;
                                }
							}
						}
						if(!f) {
							ans = max(ans,re);
						}

					}
                }
			}
        }
        printf("%d\n",ans);
	}
