typedef long long LL;

int main(){
  int d,w;
  while(1){
    cin >> d >> w;
    if(d==0) return 0;
    vector<vector<int>> e(d,vector<int>(w));
    for(int i=0;i<d;i++){
      for(int j=0;j<w;j++){
        cin >> e[i][j];
      }
    }
    vector<vector<vector<int>>> yokomin(d,vector<vector<int>>(w,vector<int>(w,10)));
    vector<vector<vector<int>>> tatemin(w,vector<vector<int>>(d,vector<int>(d,10)));
    for(int i=0;i<d;i++){
      for(int j=0;j<w;j++){
        for(int k=j+2;k<w;k++){
          for(int l=j;l<=k;l++){
            yokomin[i][j][k]=min(yokomin[i][j][k],e[i][l]);
          }
        }
      }
    }
    for(int i=0;i<w;i++){
      for(int j=0;j<d;j++){
        for(int k=j+2;k<d;k++){
          for(int l=j;l<=k;l++){
            tatemin[i][j][k]=min(tatemin[i][j][k],e[l][i]);
          }
        }
      }
    }
    vector<vector<vector<int>>> yokomax(d,vector<vector<int>>(w,vector<int>(w,0)));
    for(int i=0;i<d;i++){
      for(int j=0;j<w;j++){
        for(int k=j;k<w;k++){
          for(int l=j;l<=k;l++){
            yokomax[i][j][k]=max(yokomax[i][j][k],e[i][l]);
          }
        }
      }
    }
    //  添え磁気圏
    vector<vector<int>> ruiseki(d+1,vector<int>(w+1,0));
    for(int i=1;i<=d;i++){
      for(int j=1;j<=w;j++){
        ruiseki[i][j]=ruiseki[i-1][j]+ruiseki[i][j-1]-ruiseki[i-1][j-1]+e[i-1][j-1];
      }
    }
    int ans=0;
    int mi=10,ma;
    for(int i=0;i<d;i++){
      for(int j=0;j<w;j++){
        for(int k=i+2;k<d;k++){
          for(int l=j+2;l<w;l++){
            mi=yokomin[i][j][l];
            mi=min(mi,yokomin[k][j][l]);
            mi=min(mi,tatemin[j][i][k]);
            mi=min(mi,tatemin[l][i][k]);
            ma=0;
            for(int x=i+1;x<=k-1;x++){
              ma=max(ma,yokomax[x][j+1][l-1]);
            }
            if(mi>ma){
              ans=max(ans,mi*(k-i-1)*(l-j-1)-(ruiseki[k][l]-ruiseki[i+1][l]-ruiseki[k][j+1]+ruiseki[i+1][j+1]));
            }
          }
        }
      }
    }
    cout << ans << endl;
  }
  return 0;
}

