class step {
	struct It {
		int x, s; It(int _x, int _s) :x(_x), s(_s) { assert(s); }
		int operator*() { return x; } void operator++() { x += s; }
		bool operator!=(It& i) { return 0 < s ? x < i.x : x >= i.x; }
	}; int x, y, s;
