int solve(int h, int w) {
	VVI a = ini[{h, w}];

	auto check = [=](Point p1, Point p2) {
		int ma = -inf, mi = inf;
		FOR(x, p1.x, p2.x + 1)FOR(y, p1.y, p2.y + 1) {
			if (x == p1.x || x == p2.x || y == p1.y || y == p2.y) {
				chmin(mi, a[y][x]);
			} else {
				chmax(ma, a[y][x]);
			}
		}

		if (mi <= ma) {
			return -inf;
		} else {
			int sum = 0;
			FOR(x, p1.x + 1, p2.x)FOR(y, p1.y + 1, p2.y) {
				sum += (mi - a[y][x]);
			}
			return sum;
		}
	};

	int ans = -inf;
	rep(x1, w)rep(y1, h) {
		FOR(x2, x1 + 2, w)FOR(y2, y1 + 2, h) {
			chmax(ans, check({ x1,y1 }, { x2,y2 }));
		}
	}
	return ans == -inf ? 0 : ans;
}

int main() {
	int d, w;
	while (true) {
		d = ini; w = ini;
		if (!d && !w)break;
		out(solve(d, w));
	}
}

