int dx[8]={0,1,1,1,0,-1,-1,-1};
int dy[8]={-1,-1,0,1,1,1,0,-1};
bool sign;
int xl,xr,yl,yr,num,bound;
void dfs(int x,int y)
{
	if(x==1||y==1||x==n||y==m)
	{
		sign=0;
		return;
	}
	num++;
	xl=min(xl,x);
	xr=max(xr,x);
	yl=min(yl,y);
	yr=max(yr,y);
	vis[x*100+y]=1;
	int nx,ny;
	for(int i=0;i<8;i++)
	{
		nx=x+dx[i];
		ny=y+dy[i];
		if(vis[nx*100+ny])continue;
		if(ma[nx][ny]<=k)
		{
			if(nx==1||ny==1||nx==n||ny==m)
			{
				sign=0;
				return;
			}
			else
			{
				dfs(nx,ny);
				if(sign==0)return;
			}
		}
		else bound=min(bound,ma[nx][ny]);
	}
}
int main()
{
	while(scanf("%d%d",&n,&m)!=EOF)
	{
		if(n==0)break;
		ans=0;
