				scanf("%d",&ma[i][j]);
			}
		}
		for(k=0;k<=9;k++)
		{
			memset(vis,0,sizeof(vis));
			for(int i=2;i<=n-1;i++)
			{
				for(int j=2;j<=m-1;j++)
				{
					if(ma[i][j]<=k&&vis[i*100+j]==0)
					{
						sign=1;
						xl=i;xr=i;
						yl=j;yr=j;
						num=0;
						bound=10;
						dfs(i,j);
						if(sign&&(xr-xl+1)*(yr-yl+1)==num)
						{
							int sum=0;
							for(int p=xl;p<=xr;p++)
								for(int q=yl;q<=yr;q++)
									sum+=(bound-ma[p][q]);
							ans=max(ans,sum);
						}
					}
				}
			}
		}
		printf("%d\n",ans);
	}
	return 0;
}

