bool isPond(int sx,int sy,int width,int height){
  //std::cout << "start:(" << sx << ',' << sy << ") width = " << width << " height=" << height << '\n';
  int max = 0;
  min = 10;
  for(int k=sy;k<height+sy;k++){
    int d1 = garden[k][sx];
    int d2 = garden[k][sx+width-1];
    if(d1>d2) d1 = d2;
    if(min>d1) min = d1;
  }
  for(int k=sx;k<width+sx;k++){
    int d1 = garden[sy][k];
    int d2 = garden[sy+height-1][k];
    if(d1>d2) d1=d2;
    if(min>d1) min = d1;
  }

  for(int i=sx+1;i<width+sx-1;i++){
    for(int j=sy+1;j<height+sy-1;j++){
      int d = garden[j][i];
      if(max<d) max = d;
    }
  }

  //std::cout << "  min=" << min << "  max=" << max << '\n';

  if(min>max) return true;
  else return false;
}


