vector< vector<int> > field;

int calc_agp(int row, int col, int rr, int cr) {
	int lim = (1 << 28);
	int sum,wmax;
	sum = wmax = 0;
	for (int i = 0; i < rr; ++i) {
		for (int j = 0; j < cr; ++j) {
			if (i == 0||j == 0||i == rr-1||j == cr-1) {
				lim = min(field[row+i][col+j],lim);
			}
			else {
				wmax = max(field[row+i][col+j],wmax);
				sum += field[row+i][col+j];
			}
		}
	}
	if (wmax >= lim) {
		return 0;
	}
	return ((lim*(rr-2)*(cr-2))-sum);
}

