class Solve {
private:
    using vi = std::vector<int>;
    using vvi = std::vector<vi>;
    using vb = std::vector<bool>;
    using vvb = std::vector<vb>;

    int D, W;
    vvi height;
    vvb visited;
    int calcMin(int t, int b, int l, int r)
    {
        int min{100};
        for (int i{t - 1}; i <= b + 1; i++)
            min = std::min({min, height[i][l - 1], height[i][r + 1]});
        for (int i{l - 1}; i <= r + 1; i++)
            min = std::min({min, height[t - 1][i], height[b + 1][i]});
        return min;
    }

    int calcCap(int t, int b, int l, int r, int h)
    {
        int ret{};
        for (int i{t}; i <= b; i++)
            for (int j{l}; j <= r; j++)
            {
                if (height[i][j] >= h) return -1;
                ret += h - height[i][j];
            }
        return ret;
    }
public:
    bool is_last_{};
    Solve()
    {
        scanf("%d%d", &D, &W);
        if (D == 0 && W == 0)
        {
            is_last_ = true;
            return;
        }
        height.resize(D + 2, vi(W + 2, -1));
        for (int i{1}; i <= D; i++)
            for (int j{1}; j <= W; j++)
                scanf("%d", &height[i][j]);
        int max{};
        for (int t{2}; t < D; t++)
            for (int b{t}; b < D; b++)
                for (int l{2}; l < W; l++)
                    for (int r{l}; r < W; r++)
                    {
                        int h{calcMin(t, b, l, r)};
                        max = std::max(max, calcCap(t, b, l, r, h));
                    }
        printf("%d\n", max);
    }
};

