						//遍历边界
						int y,x;
						int outmii=100,inmiv=-1;
						for(x=0;x<wid;x++)	//水平向右遍历
						{
							if(tbl[i][x+j]<outmii) outmii=tbl[i][x+j];
							if(tbl[i+deep-1][x+j]<outmii) outmii=tbl[i+deep-1][x+j];
						}
						for(y=0;y<deep;y++)
						{
							if(tbl[y+i][j]<outmii) outmii = tbl[y+i][j];
							if(tbl[y+i][j+wid-1]<outmii) outmii = tbl[y+i][j+wid-1];
						}

						//遍历区域
						int sum=0;
						for(int x=1;x<wid-1;x++){
							for(int y=1;y<deep-1;y++){
								if(tbl[y+i][x+j]>inmiv) inmiv = tbl[y+i][x+j];
								sum+=outmii-tbl[y+i][x+j];
							}
						}

					//	if(i==1&&j==1&&wid==5&&deep==3){
					//		cout<<sum<<" "<<outmii<<" "<<inmiv<<endl;
					//	}

						if(inmiv >= outmii) continue;
						else{
							ans=max(ans,sum);
						} 
					}
				}
			}
		} 
		cout<<ans<<endl;
	}
} 

