#include <bits/stdc++.h>
using namespace std;


int solve(int H, int W) {
    vector<vector<int>> field(H, vector<int>(W));
    for (int h = 0; h < H; h++) {
        for (int w = 0; w < W; w++) {
            cin >> field[h][w];
        }
    }


    int ret = 0;

    for (int sh = 0; sh < H; sh++) {
        for (int sw = 0; sw < W; sw++) {
            for (int eh = sh+2; eh < H; eh++) {
                for (int ew = sw+2; ew < W; ew++) {
                    int outer = 100;
                    int inner = -1;
                    int sum = 0;
                    for (int h = 0; h < H; h++) {
                        for (int w = 0; w < W; w++) {
                            if (sh <= h && h <= eh && sw <= w && w <= ew) {
                                if (h == sh || h == eh || w == sw || w == ew) {
                                    outer = min(outer, field[h][w]);
                                } else {
                                    sum -= field[h][w];
                                    inner = max(inner, field[h][w]);
                                }
                            }
                            if (h == H-1 && w == W-1 && outer > inner) {
                                sum += outer * (eh - sh - 1) * (ew - sw - 1);
                                ret = max(sum, ret);
                            } 
                        }
                    }
                }
            }
        }
    }
    return ret;
}

int main(void){
    int h, w;
    vector<int> ans;
    while (1) {
        cin >> h >> w;
        if (!h && !w) break;
        ans.push_back(solve(h, w));
    }
    for (auto &i : ans) {
        cout << i << endl;
    }

}

