int main()
{
    while (true) {
        int d;
        int w;

        cin >> d >> w;
        if (d == 0 && w == 0) {
            break;
        }

        vector<vector<int>> v;
        v.resize(static_cast<size_t>(d));
        for (auto i = 0; i < d; ++i) {
            v[i].resize(static_cast<size_t>(w));
            for (auto j = 0; j < w; ++j) {
                cin >> v[i][j];
            }
        }

        int maxCount{0};
        for (int i1 = 0; i1 < d - 2; ++i1) {
            for (int i2 = i1 + 2; i2 < d; ++i2) {
                for (int j1 = 0; j1 < w - 2; ++j1) {
                    for (int j2 = j1 + 2; j2 < w; ++j2) {
                        // find min of edge
                        int mine{999999999};
                        for (int x = i1; x <= i2; ++x) {
                            mine = min(mine, v[x][j1]);
                            mine = min(mine, v[x][j2]);
                        }
                        for (int y = j1; y <= j2; ++y) {
                            mine = min(mine, v[i1][y]);
                            mine = min(mine, v[i2][y]);
                        }
                        int count{0};
                        bool invalid{false};
                        for (int x = i1 + 1; x < i2; ++x) {
                            for (int y = j1 + 1; y < j2; ++y) {
                                if (mine <= v[x][y]) {
                                    invalid = true;
                                    break;
                                }
                                count += mine - v[x][y];
                            }
                            if (invalid) {
                                break;
                            }
                        }
                        if (!invalid) {
                            maxCount = max(maxCount, count);
                        }
                    }
                }
            }
        }
        cout << maxCount << endl;
    }
}

