int solve(vector<vector<int>> field, int tate, int yoko) {
    int ret = 0;
    for (int i = 0; i <= field.size() - tate; i++) {
        for (int j = 0; j <= field[i].size() - yoko; j++) {
            // 堀の最小
            int minHori = 1000000007;
            for (int k = i; k < i + tate; k++) {
                minHori = min(field[k][j], minHori);
            }
            for (int k = i; k < i + tate; k++) {
                minHori = min(field[k][j + yoko - 1], minHori);
            }
            for (int l = j; l < j + yoko; l++) {
                minHori = min(field[i][l], minHori);
            }
            for (int l = j; l < j + yoko; l++) {
                minHori = min(field[i + tate - 1][l], minHori);
            }
            int poolWater = 0;
            // 内部の最大
            for (int k = i + 1; k < i + tate - 1; k++) {
                for (int l = j + 1; l < j + yoko - 1; l++) {
                    if (field[k][l] >= minHori) goto fackin;
                    // cout << "minHori" << minHori << "tate" << tate << "yoko" << yoko << "field"
                    //      << field[k][l] << endl;
                    poolWater += minHori - field[k][l];
                    // cout << poolWater << endl;
                }
            }
            ret = max(poolWater, ret);
        fackin:;
        }
    }
    return ret;
}

