int getCap(int lx, int rx, int ty, int dy) {
    int out = iINF, inside = 0;
    for (int i = lx; i <= rx; i++) {
        out = min(out, e[ty][i]);
        out = min(out, e[dy][i]);
    }
    for (int i = ty; i <= dy; i++) {
        out = min(out, e[i][lx]);
        out = min(out, e[i][rx]);
    }

    for (int x = lx + 1; x < rx; x++) {
        for (int y = ty + 1; y < dy; y++) {
            inside = max(inside, e[y][x]);
        }
    }

    if (out > inside) {
        int res = 0;
        for (int x = lx + 1; x < rx; x++) {
            for (int y = ty + 1; y < dy; y++) {
                res += (out - e[y][x]);
            }
        }
        return res;
    } else {
        return 0;
    }
}

